mapper.select:
SELECT COUNT(*) FROM menu_price

	WHERE ${cate} = #{input}	


	INNER JOIN menu
		ON menu_price.menu_code = menu.menu_code
	WHERE menu.${cate} = #{input}

SELECT menu_price_code , mp.menu_code , m.menu_name , tempCate.category_small AS tempCateName , sizeCate.category_small AS sizeCateName , menu_totalcost , menu_sellcost FROM menu_price AS mp INNER JOIN menu AS m ON mp.menu_code = m.menu_code INNER JOIN category AS sizeCate ON mp.size_category = sizeCate.category_code INNER JOIN category AS tempCate ON mp.temp_category = tempCate.category_code
WHERE m.${cate} = #{input}
ORDER BY mp.menu_code DESC
LIMIT ${start}, ${pagePerRow}

mapper.insert:
INSERT INTO
	menu_price (
		menu_price_code
		, menu_code
		, temp_category
		, size_category
		, menu_totalcost
		, menu_sellcost
	) VALUSE (
		#{menuCode}
		, #{tempCategory}
		, #{sizeCategory}
		, #{menuTotalCost}
		, #{menuCode}
		, #{menuSellCost}
	)
