mapper.select:
SELECT material.material_code, menu.menu_name, material.menu_code, tempcategory.category_small AS tempcate, material.temp_category_code, sizecategory.category_small AS sizecate, material.size_category_code, item.item_name, material.item_code, material.material_measure, material.material_cost
FROM material INNER JOIN menu ON material.menu_code = menu.menu_code INNER JOIN item ON material.item_code = item.item_code INNER JOIN category AS tempcategory ON material.temp_category_code = tempcategory.category_code INNER JOIN category AS sizecategory ON material.size_category_code = sizecategory.category_code
WHERE material.material_code = #{materialCode}
